/* Usage: ---------------------------------------------

<div className="github-Loader">
  <span className="github-Spinner"></span>
</div>

--------------------------------------------------- */
.github-Loader {
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 32px;
  color: #6f42c1;
}
.tooltip .github-Loader {
  color: inherit;
}
.github-Spinner {
  position: relative;
  display: inline-block;
  vertical-align: middle;
  width: 2em;
  height: 2em;
}
.github-Spinner::before {
  display: inline-block;
  width: inherit;
  height: inherit;
  content: "\f008";
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  line-height: 2;
  text-align: center;
  -webkit-font-smoothing: antialiased;
}
.github-Spinner::after {
  content: "";
  position: absolute;
  width: inherit;
  height: inherit;
  top: 0;
  left: 0;
  border-right: 2px dotted;
  border-bottom: 2px dotted;
  border-left: 2px solid transparent;
  border-radius: 1em;
  animation: github-Spinner-animation 4s linear 12, github-Spinner-animation 4s 48s steps(12) 100;
}
@keyframes github-Spinner-animation {
  100% {
    transform: rotate(360deg);
  }
}
