/* Usage: ---------------------------------------------

<div className="github-Message">
  <div className="github-Message-wrapper">
    <h1 className="github-Message-title">Whooops</h1>
    <p className="github-Message-description">Bad things happened</p>
    <p className="github-Message-action">
      <button className="github-Message-button btn btn-primary">Get me out of here</button>
    </p>
  </div>
</div>

--------------------------------------------------- */
.github-Message {
  flex: 1;
  display: flex;
  overflow-x: hidden;
  overflow-y: auto;
}
.github-Message-wrapper {
  margin: auto;
  padding: 28px;
  text-align: center;
  width: 100%;
}
.github-Message-title {
  color: #F5F5F5;
  font-size: 1.5em;
  margin-bottom: .5em;
}
.github-Message-description {
  font-size: 1.2em;
  line-height: 1.4;
  margin-bottom: 0;
}
pre.github-Message-description {
  text-align: left;
}
.github-Message-longDescription {
  font-size: 1.1em;
  line-height: 1.4;
  text-align: left;
  margin-bottom: 0;
}
.github-Message-action {
  margin-top: 28px;
}
.github-Message-button {
  margin: 7px;
}
