css:
atom-text-editor .gutter .line-number.git-line-modified {
  border-left: 2px solid #e5b567;
  padding-left: calc(0.5em - 2px);
}
atom-text-editor .gutter .line-number.git-line-added {
  border-left: 2px solid #b4c973;
  padding-left: calc(0.5em - 2px);
}
atom-text-editor .gutter .line-number.git-line-removed:before,
atom-text-editor .gutter .line-number.git-previous-line-removed:before {
  position: absolute;
  left: 0;
  height: 0;
  width: 0;
  content: "";
  border: solid transparent;
  border-left-color: #c45330;
  border-width: 4px;
  margin-top: -4px;
  pointer-events: none;
}
atom-text-editor .gutter .line-number.git-line-removed:before {
  bottom: -4px;
}
atom-text-editor .gutter .line-number.git-previous-line-removed:before {
  top: 0;
}
atom-text-editor .gutter.git-diff-icon .line-number {
  border-left-width: 0;
  padding-left: 1.4em;
}
atom-text-editor .gutter.git-diff-icon .line-number:before {
  font-family: 'Octicons Regular';
  src: url("icons/octicons.woff") format("woff");
  font-weight: normal;
  font-style: normal;
  content: "";
  display: inline-block;
  position: absolute;
  top: .2em;
  left: .4em;
  height: 0px;
  width: 1em;
  font-size: .75em;
}
atom-text-editor .gutter.git-diff-icon .line-number.git-line-modified:before {
  content: "\f052";
  color: #e5b567;
}
atom-text-editor .gutter.git-diff-icon .line-number.git-line-added:before {
  content: "\f05d";
  color: #b4c973;
}
atom-text-editor .gutter.git-diff-icon .line-number.git-line-removed:before,
atom-text-editor .gutter.git-diff-icon .line-number.git-previous-line-removed:before {
  border: none;
  content: "\f0ca";
  color: #c45330;
}
atom-text-editor .gutter.git-diff-icon .line-number.git-line-removed:before {
  top: 1em;
}
atom-text-editor .gutter.git-diff-icon .line-number.git-previous-line-removed:before {
  top: 0;
}
