css:
.tab-bar {
  display: flex;
  -webkit-user-select: none;
  margin: 0;
  /* Drag and Drop */
}
.tab-bar .tab {
  font-size: 11px;
  position: relative;
  padding-left: 10px;
  padding-right: 24px;
  -webkit-user-drag: element;
  flex: 1;
  max-width: 175px;
  min-width: 40px;
  transition: max-width 0.25s ease-in-out;
}
.tab-bar .tab.active {
  flex: 2;
  width: -webkit-fit-content;
}
.tab-bar .tab.active .title {
  padding-right: 10px;
}
.tab-bar .tab .title {
  overflow: hidden;
  white-space: pre;
  text-overflow: ellipsis;
}
.tab-bar .tab .title.temp {
  font-style: italic;
}
.tab-bar .tab .hide-icon.icon:before {
  display: none;
}
.tab-bar .tab .close-icon {
  position: absolute;
  top: 1px;
  right: 10px;
  cursor: default;
}
.tab-bar .tab .close-icon::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 12px;
  width: 12px;
  height: 12px;
  content: "\f081";
}
.tab-bar .tab.modified:hover .close-icon {
  color: #4c84ac;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 11px;
  right: 11px;
  width: 8px;
  height: 8px;
  border: 2px solid #4c84ac;
  border-radius: 12px;
}
.tab-bar .tab.modified:not(:hover) .close-icon:before {
  display: none;
}
.tab-bar .placeholder {
  position: relative;
  top: 0;
  padding: 0;
  height: 3rem;
  z-index: 999;
  list-style: none;
  background: #4c84ac;
  pointer-events: none;
}
.tab-bar .placeholder:before {
  content: "";
  position: absolute;
  width: 2px;
  margin: -1px;
  padding: 0;
  height: inherit;
  background: inherit;
}
.tab-bar .placeholder:after {
  content: "";
  position: absolute;
  top: 3rem;
  margin-top: -1px;
  margin-left: -2px;
  z-index: 9999;
  width: 4px;
  height: 4px;
  background: inherit;
  border-radius: 4px;
  border: 1px solid transparent;
}
.tab-bar.hidden {
  display: none;
}
