css:
.styleguide {
  position: relative;
  display: flex;
  flex-direction: column;
}
.styleguide a {
  text-decoration: underline;
}
.styleguide-controls {
  position: absolute;
  right: 14px;
  top: 14px;
  z-index: 100;
}
.styleguide-header {
  padding: 21px;
  border-bottom: 1px solid #1e1e1e;
}
.styleguide-header h1 {
  font-size: 2em;
  margin: 0 0 .5em 0;
  color: #F5F5F5;
}
.styleguide-header p {
  font-size: 1.2em;
}
.styleguide-header p:last-of-type {
  margin-bottom: 0;
}
.styleguide-sections {
  flex: 1;
  overflow: auto;
}
.styleguide-sections > section {
  background-color: #191919;
  padding: 0;
  border-bottom: 1px solid #1e1e1e;
  border-top: none;
}
.styleguide-sections > section:last-child {
  margin-bottom: 0;
}
.styleguide-sections > section.collapsed {
  background-color: #1e1e1e;
}
.styleguide-sections > section.collapsed > .section-heading {
  display: block;
  margin: 0;
  padding-bottom: 21px;
  color: #d6d6d6;
}
.styleguide-sections > section.collapsed > .section-heading:hover {
  color: #F5F5F5;
  background-color: #404040;
}
.styleguide-sections > section.collapsed > .section-heading:active {
  background-color: #1e1e1e;
}
.styleguide-sections > section.collapsed > * {
  display: none;
}
.styleguide-sections .section-heading.section-heading {
  padding: 21px 21px 0 21px;
  cursor: pointer;
  font-weight: normal;
  font-size: 1.8em;
  color: #F5F5F5;
}
.styleguide-sections section > h2 {
  font-size: 1.5em;
  line-height: 1.2;
  margin: 1em 21px 0 21px;
  color: #F5F5F5;
}
.styleguide-sections section > p {
  font-size: 1.1em;
  margin: 0.5em 21px 1em 21px;
}
.styleguide .example {
  display: flex;
  flex-wrap: wrap;
  border-radius: 2px;
  padding: 7px;
}
.styleguide .example .example-rendered,
.styleguide .example .example-code {
  position: relative;
  flex: 1 1 300px;
  min-width: 0;
  margin: 7px;
  border-radius: 2px;
  border: 1px solid #1e1e1e;
}
.styleguide .example .example-rendered {
  padding: 14px;
  background: #1e1e1e;
  overflow: hidden;
}
.styleguide .example .example-code {
  background-color: #282c34;
}
.styleguide .example .example-code pre {
  border: none;
  background-color: inherit;
}
